const cron = require('node-cron');
const { exec } = require('child_process');
const fs = require('fs');
const path = require('path');

class TestScheduler {
    constructor() {
        this.logFile = path.join(__dirname, '../logs/test-execution.log');
        this.ensureLogDirectory();
    }

    ensureLogDirectory() {
        const logDir = path.dirname(this.logFile);
        if (!fs.existsSync(logDir)) {
            fs.mkdirSync(logDir, { recursive: true });
        }
    }

    log(message) {
        const timestamp = new Date().toISOString();
        const logMessage = `[${timestamp}] ${message}\n`;

        console.log(logMessage.trim());
        fs.appendFileSync(this.logFile, logMessage);
    }

    runTests() {
        this.log('Starting Playwright test execution');

        return new Promise((resolve, reject) => {
            exec('npx playwright test --reporter=html,line', 
                { cwd: path.join(__dirname, '..') },
                (error, stdout, stderr) => {
                    if (error) {
                        this.log(`Test execution failed: ${error.message}`);
                        reject(error);
                    } else {
                        this.log('Test execution completed successfully');
                        this.log(`Output: ${stdout}`);
                        resolve(stdout);
                    }

                    if (stderr) {
                        this.log(`Stderr: ${stderr}`);
                    }
                }
            );
        });
    }

    startScheduler() {
        // 多种调度选项
        const schedules = {
            daily: '0 2 * * *',      // 每天凌晨2点
            hourly: '0 * * * *',     // 每小时
            every30min: '*/30 * * * *', // 每30分钟
            businessHours: '0 9-17 * * 1-5' // 工作日9-17点
        };

        // 使用每日调度
        cron.schedule(schedules.daily, async () => {
            try {
                await this.runTests();
                // 可以添加邮件通知、Slack通知等
                this.sendNotification('Tests completed successfully');
            } catch (error) {
                this.sendNotification(`Tests failed: ${error.message}`, 'error');
            }
        });

        this.log(`Test scheduler started with schedule: ${schedules.daily}`);
    }

    sendNotification(message, type = 'info') {
        // 实现通知逻辑（邮件、Slack、Teams等）
        this.log(`Notification [${type}]: ${message}`);
    }
}

// 启动调度器
const scheduler = new TestScheduler();
scheduler.startScheduler();
